mapper.select:
    		select *
    		from purge_task
    	
	         
	           AND commit_time >=  #{startdate}
	         
	          
	           AND commit_time <=  #{enddate}
	         
    	
    	 
    	 	order by #{sortBy}
    	 
    	  
    	 	#{sortOrder}
    	 
    	 
    	 	limit #{limit}
    	 
    	 
    	 	offset #{offset}

    		select *
    		from purge_task
    	
	         
	           AND name = #{name}
	         
	         
	           AND target = #{target}
	         
	         
	           AND action = #{action}
	         
	          
	           AND customer_name = #{customerName}
